const url="http://localhost:3000/api/cameras/",getCams=function(){fetch(url).then((function(response){response.json().then((function(data){let affichageList="";for(let list of data)affichageList+=`\n        <div class=" mt-1 ">   \n          <div class=" card border-end ">\n            <a href="./produit.html?id=${list._id}" class=" text-dark text-decoration-none">\n              <div class="card-body">\n                  <div class=" blockImg ">\n                      <img src="${list.imageUrl}" class="img-fluid shadow-1-strong rounded-1"  alt="image de l'appareil photo ${list.name} ">  \n                  </div>\n                  <div class = " card-back d-flex justify-content-between ">\n                  <h2 class="card-title">${list.name}</h2>\n                  <p class="card-price ">${list.price}</p>\n                  </div>\n              </div>\n            </a>\n          </div>\n        </div>`;document.querySelector(".produit").innerHTML=affichageList})).catch((function(err){console.log("erreur: "+err)}))}))};async function imgCarousel(){let response=await fetch(url),data=await response.json(),affichageList=`   \n          <div class="carousel-item active img-Carousel">\n               <img src="${data[2].imageUrl}" class="d-block w-100 " alt="...">\n          </div>`;try{for(let list of data)affichageList+=`   \n            <div class="carousel-item  img-Carousel">\n          <img src="${list.imageUrl}" class="d-block w-100  " alt="...">\n            </div>`;document.querySelector(".carousel-inner").innerHTML=affichageList}catch(err){console.log("erreur: "+err)}}getCams(),imgCarousel();